import React, { useState } from 'react';

const Contact = () => {
  const [modalVisible, setModalVisible] = useState(false);

  const openModal = () => {
    setModalVisible(true);
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <div className="contact bg-white py-12 px-4">
      <div className="max-w-2xl mx-auto text-center">
        <div className="mb-4">
          <h2 className="text-3xl font-bold mb-4">Contact Us</h2>
          <p className="mb-4 text-gray-600">
            Cancellations accepted up to 24 hours before the scheduled service. Within 24 hours of the service, cancellations will be charged 50% of the scheduled cost.
          </p>
          <button className="whatsapp-button py-2 px-4 bg-yellow-600 text-white font-bold rounded-full inline-flex items-center justify-center" onClick={openModal}>
            Contact Us on Email
          </button>
        </div>

        <div id="emailModal" className={`modal ${modalVisible ? 'visible' : ''}`}>
          <div className="modal-content bg-white p-8 rounded-lg shadow-lg">
            <span className="close cursor-pointer" onClick={closeModal}>&times;</span>
            <form id="emailForm" action="https://formsubmit.co/d223b3f1af5402deaf12edf81c9189df" method="POST">
              <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-2">Name:</label>
              <input type="text" id="name" name="name" required className="w-full border border-gray-300 rounded-lg p-2 mb-4" />

              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-2">Email:</label>
              <input id="email" type="email" placeholder="Email-ID" name="email" required className="w-full border border-gray-300 rounded-lg p-2 mb-4" />

              <label htmlFor="emailContent" className="block text-sm font-medium text-gray-700 mb-2">Email Content:</label>
              <textarea id="emailContent" name="emailContent" required className="w-full border border-gray-300 rounded-lg p-2 mb-4"></textarea>

              <input type="submit" value="Submit" className="bg-blue-500 text-white py-2 px-4 rounded-full cursor-pointer" />
            </form>
          </div>
        </div>

        <div className="mb-4">
          <p className="footer-text mb-4 text-gray-600">Hours</p>
          <select className="border border-gray-300 rounded-lg p-2">
            <option>Open today 09:00 am - 05:00 pm</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default Contact;
