// PromoSection.js
import React from 'react';

const PromoSection = () => {
  return (
    <div className="promo-section text-center p-12">
      <h2 className="text-4xl mb-4">ORDER SWEET DELIGHTS</h2>
      <p className="mb-6">Explore our wide array of handcrafted sweets, each made with love and expertise. Elevate your moments with our delectable creations.</p>
      <button className="button">VISIT US NOW!</button>
    </div>
  );
};

export default PromoSection;
